import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Table, Button } from "reactstrap";

const UserLists = () => {
  const [usersList, setUsersList] = useState([]);
  useEffect(() => {
    getUser();
  }, []);

  const getUser = () => {
    fetch("http://localhost:4000/users")
      .then((response) => response.json())
      .then((result) => {
        setUsersList(result);
      });
  };

  const updateUser = (id) => {
    window.location = "/update/" + id;
  };

  const userDelete = (id) => {
    console.log(id);
    fetch("https://localhost:4000/users" + id, {
      method: "DELETE",
      // headers: {
      //   "Content-Type": "application/json",
      // },
    }).then((response) => {
      response.json();
    });
  };
  return (
    <div>
      <Link to="/create" className="btn btn-success">
        Add User
      </Link>
      <Table striped>
        <thead border="3">
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Country</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody border="3">
          {usersList.map((user) => (
            <tr key={user.id}>
              <th scope="row">{user.id}</th>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.age}</td>
              <td>{user.gender}</td>
              <td>{user.country}</td>
              <td>
                <Button
                  className="btn btn-success"
                  onClick={() => updateUser(user.id)}
                >
                  Edit
                </Button>
              </td>
              <td>
                <Button
                  className="btn btn-danger"
                  onClick={() => {
                    userDelete(user.id);
                  }}
                >
                  {" "}
                  Delete{" "}
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default UserLists;
